import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';




const KeyPad = props => {
  return (
    <div id={props.idValue} className="key-pad" onClick={props.click}>
      {props.displayValue}
    </div>
  );
};

const KeyBank = props => {
  const calcButtons = props.calckeys.map(k => (
    <KeyPad
      operatorValue={k.operator}
      displayValue={k.display}
      idValue={k.id}
      click={props.click}
    />
  ));
  return <div className="pad-bank">{calcButtons}</div>;
};

const DisplayResult = props => {
  return <div id="display">{props.showResult}</div>;
};

const regexNumbers = /\d+/g;
const regexOperator = /\+|-|\*|\/|=/g;
const regexNumberOperator = /((\d*\.\d+)|(\d+)|([\+\-\*/\(\)]))/g;
const regexLeadingZero = /^0+/g;
const regexEndsWithOperator = /[x+â€‘/]$/;

class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calckeys: calcElements,
      displayResult: "",
      displayZero: 0
    };
    this.handleClick = this.handleClick.bind(this);
    this.calculate = this.calculate.bind(this);
  }

  calculate(mathProblem) {
    console.log("mathProblem " + mathProblem);
    //const regexNumbers = /\d+/g;
    //const regexOperator = /\+|-|\*|\/|=/g;
    const regexNumberOperator = /((\d*\.\d+)|(\d+)|([\+\-\*/\(\)]))/g;
    //const regexLeadingZero = /^0+/g;

    const numberOperaterArray = mathProblem.match(regexNumberOperator);
    console.log(numberOperaterArray);

    const divideOperator = numberOperaterArray.indexOf("/");
    const multiplyOperator = numberOperaterArray.indexOf("*");
    const plusOperator = numberOperaterArray.indexOf("+");
    const subtractOperator = numberOperaterArray.indexOf("-");

    let answer = Math.round(1000000000000 * eval(mathProblem)) / 1000000000000;
    console.log(answer, this.state.displayResult);
    return answer;
  }

  handleClick(e) {

    this.setState({
      displayResult: this.state.displayResult + e.target.innerText
    });

    if (e.target.id === "clear") {
      this.setState({
        displayResult: "",
        lastClicked: ""
      });
    } else if (e.target.id === "decimal") {
      if (regexEndsWithOperator.test(this.state.displayResult)) {
        this.setState({
          lastClicked: 0,
          displayResult: this.state.displayResult + "0." + e.target.innerText
        });
      } else {
        this.setState({
          lastClicked: e.target.innerText,
          displayResult: this.state.displayResult + "0." + e.target.innerText
        });
      }
    } else if (e.target.id === "equals") {
      var problemResult = this.calculate(this.state.displayResult);
      this.setState({
        lastClicked: e.target.innerText,
        displayResult: this.state.displayResult + e.target.innerText + problemResult
      });
    } else if (e.target.id === "zero") {
      this.setState({
        displayResult: problemResult
      });
    } else {
      this.setState({
        lastClicked: e.target.innerText,
        displayResult: this.state.displayResult + e.target.innerText
      });
    }
  }

  render() {
    const displayResult = <DisplayResult showResult={this.state.displayResult} />;
    const displayZero = <DisplayResult showResult={this.state.displayZero} />;
    return (
      <div id="app-container">
        <div>
          display: {this.state.displayResult}
          <br />
          lastclicked: {this.state.lastClicked}
        </div>
        <h1>Calculator</h1>
        <KeyBank calckeys={this.state.calckeys} click={this.handleClick} />
        {this.state.displayResult == "" ? displayZero : displayResult}
      </div>
    );
  }
}

const calcElements = [
  {
    operator: "AC",
    display: "AC",
    id: "clear"
  },
  {
    operator: "\u00F7",
    display: "/",
    id: "divide"
  },
  {
    operator: "\u00d7",
    display: "*",
    id: "multiply"
  },
  {
    operator: "-",
    display: "-",
    id: "subtract"
  },
  {
    operator: "+",
    display: "+",
    id: "add"
  },
  {
    operator: "=",
    display: "=",
    id: "equals"
  },
  {
    operator: ".",
    display: ".",
    id: "decimal"
  },
  {
    operator: 0,
    display: 0,
    id: "zero"
  },
  {
    operator: 1,
    display: 1,
    id: "one"
  },
  {
    operator: 2,
    display: 2,
    id: "two"
  },
  {
    operator: 3,
    display: 3,
    id: "three"
  },
  {
    operator: 4,
    display: 4,
    id: "four"
  },
  {
    operator: 5,
    display: 5,
    id: "five"
  },
  {
    operator: 6,
    display: 6,
    id: "six"
  },
  {
    operator: 7,
    display: 7,
    id: "seven"
  },
  {
    operator: 8,
    display: 8,
    id: "eight"
  },
  {
    operator: 9,
    display: 9,
    id: "nine"
  }
];




