import React from 'react';
import './App.css';

//const regexNumbers = /\d+/g;
//const regexOperator = /\+|-|\*|\/|=/g;
const regexNumberOperator = /((\d*\.\d+)|(\d+)|([+\-*/()]))/g;
const regexLeadingZero = /^0+/g;
const regexEndsWithOperator = /[x+‑/]$/;

const KeyPad = props => {
  return (
    <div id={props.idValue} className="key-pad" onClick={props.click}>
      {props.displayValue}
    </div>
  );
};

const KeyBank = props => {
  const calcButtons = props.calckeys.map(k => (
    <KeyPad key={k.id}
      operatorValue={k.operator}
      displayValue={k.display}
      idValue={k.id}
      click={props.click}
    />
  ));
  return <div className="pad-bank">{calcButtons}</div>;
};

const DisplayFormula = props => {
  return <div id="displayFormula">{props.showFormula}</div>;
};

const DisplayResult = props => {
  return <div id="display">{props.showResult}</div>;
};



class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calckeys: calcElements,
      displayFormula: "",
      displayResult: 0,
      lastKeyPadClicked: ""
    };
    this.handleClick = this.handleClick.bind(this);
    this.calculate = this.calculate.bind(this);
  }

  calculate(mathProblem) {
    console.log("mathProblem " + mathProblem);
    //const regexNumbers = /\d+/g;
    //const regexOperator = /\+|-|\*|\/|=/g;

    const numberOperaterArray = mathProblem.match(regexNumberOperator);
    console.log(numberOperaterArray);

    //const divideOperator = numberOperaterArray.indexOf("/");
    //const multiplyOperator = numberOperaterArray.indexOf("*");
    //const plusOperator = numberOperaterArray.indexOf("+");
    //const subtractOperator = numberOperaterArray.indexOf("-");

    // eslint-disable-next-line
    let answer = Math.round(1000000000000 * eval(mathProblem)) / 1000000000000;
    console.log(answer, this.state.displayResult);
    return answer;
  }

  handleClick(e) {
    this.setState({
      displayFormula: this.state.displayFormula + e.target.innerText,
      displayResult: this.state.displayResult + e.target.innerText
    });
    //--------------CLEAR-----------------------
    if (e.target.id === "clear") {
      this.setState({
        displayFormula: "",
        displayResult: 0,
        lastKeyPadClicked: ""
      }); //-------------DECIMAL------------------------
    } else if (e.target.id === "decimal") {
      if (regexEndsWithOperator.test(this.state.displayResult)) {
        this.setState({
          lastKeyPadClicked: 0,
          displayResult: this.state.displayResult + "0." + e.target.innerText
        });
      } else {
        this.setState({
          lastKeyPadClicked: e.target.innerText,
          displayResult: this.state.displayResult + "0." + e.target.innerText
        });
      } //------------EQUALS-------------------------
    } else if (e.target.id === "equals") {
      var problemResult = this.calculate(this.state.displayFormula);
      this.setState({
        lastKeyPadClicked: problemResult
      }); //--------------ZERO-----------------------
    } else if (e.target.id === "zero") {
      if (regexLeadingZero.test(this.state.displayResult && regexEndsWithOperator.test(this.state.lastKeyPadClicked))) {
        this.setState({
          displayFormula: e.target.innerText.slice(1)
        });
      }
      //---------------EVERYTHING ELSE----------------------
    } else {
      this.setState({
        lastKeyPadClicked: e.target.innerText,
        displayResult: this.state.displayResult + e.target.innerText
      });
    }
  }

  render() {
    return (
      <div id="app-container">
        <div>
          display: {this.state.displayResult}
          <br />
          lastKeyPadClicked: {this.state.lastKeyPadClicked}
        </div>
        <h1>Calculator</h1>
        <DisplayFormula showFormula={this.state.displayFormula} />
        <DisplayResult showLastClicked={this.state.lastKeyPadClicked} showResult={this.state.displayResult} />
        <KeyBank calckeys={this.state.calckeys} click={this.handleClick} />

      </div>
    );
  }
}

const calcElements = [
  {
    operator: "AC",
    display: "AC",
    id: "clear"
  },
  {
    operator: "\u00F7",
    display: "/",
    id: "divide"
  },
  {
    operator: "\u00d7",
    display: "*",
    id: "multiply"
  },
  {
    operator: "-",
    display: "-",
    id: "subtract"
  },
  {
    operator: "+",
    display: "+",
    id: "add"
  },
  {
    operator: "=",
    display: "=",
    id: "equals"
  },
  {
    operator: ".",
    display: ".",
    id: "decimal"
  },
  {
    operator: 0,
    display: 0,
    id: "zero"
  },
  {
    operator: 1,
    display: 1,
    id: "one"
  },
  {
    operator: 2,
    display: 2,
    id: "two"
  },
  {
    operator: 3,
    display: 3,
    id: "three"
  },
  {
    operator: 4,
    display: 4,
    id: "four"
  },
  {
    operator: 5,
    display: 5,
    id: "five"
  },
  {
    operator: 6,
    display: 6,
    id: "six"
  },
  {
    operator: 7,
    display: 7,
    id: "seven"
  },
  {
    operator: 8,
    display: 8,
    id: "eight"
  },
  {
    operator: 9,
    display: 9,
    id: "nine"
  }
];

export default Calculator;
