import React from 'react';
import './App.css';

//const regexNumbers = /\d+/g;
const regexOperator = /[x/+‑*]/;
const regexNumberOperator = /((\d*\.\d+)|(\d+)|([+\-*/()]))/g;
//const regexLeadingZero = /^0+/g;
const regexEndsWithOperator = /[x+‑/]$/;

const KeyPad = props => {
  return (
    <div id={props.idValue} className="key-pad" onClick={props.click}>
      {props.displayValue}
    </div>
  );
};

const KeyBank = props => {
  const calcButtons = props.calckeys.map(k => (
    <KeyPad key={k.id}
      operatorValue={k.operator}
      displayValue={k.display}
      idValue={k.id}
      click={props.click}
    />
  ));
  return <div className="pad-bank">{calcButtons}</div>;
};

const DisplayFormula = props => {
  return <div id="displayFormula">{props.showFormula}</div>;
};

const DisplayResult = props => {
  return <div id="display">{props.showResult}</div>;
};



class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calckeys: calcElements,
      displayFormula: "",
      displayResult: "0",
      prevClicked: "0",
      currentClicked: "0",
      problemSolved: false,
      test: ""
    };
    this.handleClick = this.handleClick.bind(this);
    this.calculate = this.calculate.bind(this);
  }

  calculate(mathProblem) {
    console.log("mathProblem " + mathProblem);
    //const regexNumbers = /\d+/g;
    //const regexOperator = /\+|-|\*|\/|=/g;

    const numberOperaterArray = mathProblem.match(regexNumberOperator);
    console.log(numberOperaterArray);

    // eslint-disable-next-line
    let answer = Math.round(1000000000000 * eval(mathProblem)) / 1000000000000;
    console.log(answer, this.state.displayResult);
    return answer;
  }

  handleClick(e) {
    //--------------CLEAR-----------------------
    if (e.target.id === "clear") {
      this.setState({
        displayFormula: "",
        displayResult: "0",
        prevClicked: "0",
        currentClicked: "0",
        problemSolved: false,
        test: "CLEAR"
      });
      //------------OPERATOR------------------------
    } else if (e.target.id === "divide" 
    || e.target.id === "multiply" 
    || e.target.id === "add" 
    || e.target.id === "subtract") {
      var problemResult = this.calculate(this.state.displayFormula);
      this.setState({
        currentClicked: problemResult,
        prevClicked: e.target.innerText,
        displayFormula: this.state.displayFormula + "=" + problemResult,
        problemSolved: true
      });
      //-------------DECIMAL------------------------
    } else if (e.target.id === "decimal") {
      if (this.state.problemSolved === true) {
        this.setState({
          currentClicked: '0.',
          displayFormula: '0.',
          problemSolved: false,
          test: "DECRESET"
        });
      } else if (!this.state.currentClicked.includes('.')) {
        this.setState({ problemSolved: false })
        if (regexEndsWithOperator.test(this.state.displayFormula) || (this.state.currentClicked === '0' && this.state.displayFormula === '')) {
          this.setState({
            currentClicked: '0.',
            displayFormula: this.state.displayFormula + '0.',
            test: "DEC1"
          });
        } else {
          this.setState({
            currentClicked: this.state.displayFormula.match(/(-?\d+\.?\d*)$/) + '.',
            displayFormula: this.state.displayFormula + '.',
            test: "DEC2"
          });
        }
      }
      //------------EQUALS-------------------------
    } else if (e.target.id === "equals" && this.state.problemSolved === false) {
      var problemResult = this.calculate(this.state.displayFormula);
      this.setState({
        currentClicked: problemResult,
        prevClicked: e.target.innerText,
        displayFormula: this.state.displayFormula + "=" + problemResult,
        problemSolved: true
      });
      //--------------NUMBERS-----------------------
    } else {
      if (this.state.problemSolved === false) {
        debugger;
        this.setState({
          currentClicked:
            this.state.currentClicked === '0' ||
              regexOperator.test(this.state.currentClicked) ?
              e.target.innerText : this.state.currentClicked + e.target.innerText,
          displayFormula:
            this.state.currentClicked === '0' && e.target.innerText === '0' ?
              this.state.displayFormula :
              /([^.0-9]0)$/.test(this.state.displayFormula) ?
                this.state.displayFormula.slice(0, -1) + e.target.innerText :
                this.state.displayFormula + e.target.innerText,
        });
      }
    }
  }

  render() {
    return (
      <div id="app-container">
        <div>
          displayResult: {this.state.displayResult}
          <br />
          currentClicked: {this.state.currentClicked}
          <br />
          DisplayFormula: {this.state.displayFormula}
          <br />
          problemSolved: {this.state.problemSolved}
          <br />
          test: {this.state.test}
        </div>
        <h1>Calculator</h1>
        <DisplayFormula showFormula={this.state.displayFormula} />
        <DisplayResult showResult={this.state.currentClicked} />
        <KeyBank calckeys={this.state.calckeys} click={this.handleClick} />

      </div>
    );
  }
}

const calcElements = [
  {
    operator: "AC",
    display: "AC",
    id: "clear"
  },
  {
    operator: "\u00F7",
    display: "/",
    id: "divide"
  },
  {
    operator: "\u00d7",
    display: "*",
    id: "multiply"
  },
  {
    operator: "-",
    display: "-",
    id: "subtract"
  },
  {
    operator: "+",
    display: "+",
    id: "add"
  },
  {
    operator: "=",
    display: "=",
    id: "equals"
  },
  {
    operator: ".",
    display: ".",
    id: "decimal"
  },
  {
    operator: 0,
    display: 0,
    id: "zero"
  },
  {
    operator: 1,
    display: 1,
    id: "one"
  },
  {
    operator: 2,
    display: 2,
    id: "two"
  },
  {
    operator: 3,
    display: 3,
    id: "three"
  },
  {
    operator: 4,
    display: 4,
    id: "four"
  },
  {
    operator: 5,
    display: 5,
    id: "five"
  },
  {
    operator: 6,
    display: 6,
    id: "six"
  },
  {
    operator: 7,
    display: 7,
    id: "seven"
  },
  {
    operator: 8,
    display: 8,
    id: "eight"
  },
  {
    operator: 9,
    display: 9,
    id: "nine"
  }
];

export default Calculator;
